mapper.insert:
MERGE
 INTO MGCB151TS /* MGCB_사용자세션상태 */
USING DUAL
   ON (APP_CD = #{appCd}
      AND SSID = #{ssid}
      AND USER_ID = #{userId}
      AND AUMTH_TPCD = #{aumthTpcd}
      AND SS_STCD IN ('00', '20')) /* 00: 로그인, 20: 세션만료 */
 WHEN MATCHED THEN
   UPDATE SET
       SS_STCD = '00'
     , LOGIN_DTTM = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
     , EXPIRE_DTTM = SYSDATE + #{ssvaldSec} / (24*60*60)
     , SSVALD_SEC = #{ssvaldSec}
     , BROWS_INF = #{browsInf}
     , PRIVATE_KEY = null
     , PUBLIC_KEY = null
     , SYM_KEY = null
 WHEN NOT MATCHED THEN
   INSERT (
       APP_CD
     , USER_ID
     , AUMTH_TPCD
     , SSID
     , SS_STCD
     , EXPIRE_DTTM
     , SSVALD_SEC
     , LOGIN_DTTM
     , BROWS_INF
     , IP
     , LAST_MODF_DTTM
   ) VALUES (
       #{appCd}
     , #{userId}
     , #{aumthTpcd}
     , #{ssid}
     , '00'
     , SYSDATE + #{ssvaldSec} / (24*60*60)
     , #{ssvaldSec}
     , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
     , #{browsInf}
     , #{ip}
     , SYSDATE
   )
