mapper.insert:
UPDATE
  MGCB151TS /* MGCB_사용자세션상태 */ SET
       PRIVATE_KEY = #{privateKey}
     , PUBLIC_KEY = #{publicKey}
 WHERE APP_CD = #{appCd}
   AND USER_ID = #{ssuserId}
   AND AUMTH_TPCD = #{aumthTpcd}
   AND SSID = #{ssid}

UPDATE
  MGCB151TS /* MGCB_사용자세션상태 */ SET
       SYM_KEY = #{symKey}
 WHERE APP_CD = #{appCd}
   AND USER_ID = #{ssuserId}
   AND AUMTH_TPCD = #{aumthTpcd}
   AND SSID = #{ssid}

mapper.select:
SELECT 
       PRIVATE_KEY
     , SYM_KEY
  FROM MGCB151TS /* MGCB_사용자세션상태 */
 WHERE APP_CD = #{appCd}
   AND USER_ID = #{ssuserId}
   AND AUMTH_TPCD = #{aumthTpcd}
   AND SSID = #{ssid}
