mapper.update:
UPDATE /* mgkim.proto.www.cmm.mapper.CmmReloadableConfMngMapper.updateReloadState */
	MGCB401TS /* MGCB_reloadable-config상태 */ SET
		  CONF_STCD = #{confStcd}
		, APLI_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
		, APLI_RDTTM = null
		, LAST_MODF_DTTM = SYSDATE
WHERE   SITE_TPCD = #{siteTpcd}
	AND  HOSTNAME = #{hostname}
	AND  WAS_ID = #{wasId}
	AND  CONF_ID = #{confId}

mapper.delete:
DELETE /* mgkim.proto.www.cmm.mapper.CmmReloadableConfMngMapper.deleteReloadStateForReset */
FROM	 MGCB401TS /* MGCB_reloadable-config상태 */
WHERE  SITE_TPCD = #{siteTpcd}
		AND HOSTNAME = #{hostname}
		AND WAS_ID = #{wasId}
